import { concat, fn, hash } from "@ember/helper";
import didInsert from "@ember/render-modifiers/modifiers/did-insert";
import { or } from "truth-helpers";
import DButton from "discourse/components/d-button";
import concatClass from "discourse/helpers/concat-class";
import icon from "discourse/helpers/d-icon";
import { precompileTemplate } from "@ember/template-compilation";
import { setComponentTemplate } from "@ember/component";
import templateOnly from "@ember/component/template-only";
const DDefaultToast = setComponentTemplate(precompileTemplate("\n  <div class={{concatClass \"fk-d-default-toast\" (concat \"-\" (or @data.theme \"default\"))}} ...attributes>\n    {{#if @showProgressBar}}\n      <div class=\"fk-d-default-toast__progress-bar\" {{didInsert @onRegisterProgressBar}}></div>\n    {{/if}}\n    {{#if @data.icon}}\n      <div class=\"fk-d-default-toast__icon-container\">\n        {{icon @data.icon}}\n      </div>\n    {{/if}}\n    <div class=\"fk-d-default-toast__main-container\">\n      <div class=\"fk-d-default-toast__texts\">\n        {{#if @data.title}}\n          <div class=\"fk-d-default-toast__title\">\n            {{@data.title}}\n          </div>\n        {{/if}}\n        {{#if @data.message}}\n          <div class=\"fk-d-default-toast__message\">\n            {{@data.message}}\n          </div>\n        {{/if}}\n      </div>\n\n      {{#if @data.actions}}\n        <div class=\"fk-d-default-toast__actions\">\n          {{#each @data.actions as |toastAction|}}\n            {{#if toastAction.action}}\n              <DButton @icon={{toastAction.icon}} @translatedLabel={{toastAction.label}} @action={{fn toastAction.action (hash data=@data close=@close)}} class={{toastAction.class}} tabindex=\"0\" />\n            {{/if}}\n          {{/each}}\n        </div>\n      {{/if}}\n    </div>\n    <div class=\"fk-d-default-toast__close-container\">\n      <DButton class=\"btn-transparent\" @icon=\"xmark\" @action={{@close}} />\n    </div>\n  </div>\n", {
  strictMode: true,
  scope: () => ({
    concatClass,
    concat,
    or,
    didInsert,
    icon,
    DButton,
    fn,
    hash
  })
}), templateOnly(undefined, "d-default-toast:DDefaultToast"));
export default DDefaultToast;