import { precompileTemplate } from "@ember/template-compilation";
export default precompileTemplate("{{#if this.selectKit.options.showFullTitle}}\n  <div lang={{this.lang}} title={{this.title}} data-value={{this.value}} data-name={{this.name}} class=\"select-kit-selected-name selected-name choice\">\n    {{#if this.selectKit.options.formName}}\n      <input type=\"hidden\" name={{this.selectKit.options.formName}} value={{this.value}} />\n    {{/if}}\n\n    {{#if this.item.icon}}\n      {{d-icon this.item.icon}}\n    {{/if}}\n\n    <span class=\"name\">\n      {{this.label}}\n    </span>\n\n    {{#if this.shouldDisplayClearableButton}}\n      <DButton @icon=\"xmark\" @action={{fn this.selectKit.deselect this.item}} @ariaLabel=\"clear_input\" class=\"btn-clear\" />\n    {{/if}}\n  </div>\n{{else}}\n  {{#if this.item.icon}}\n    <div lang={{this.lang}} class=\"select-kit-selected-name selected-name choice\">\n      {{d-icon this.item.icon}}\n    </div>\n  {{/if}}\n{{/if}}", {
  moduleName: "select-kit/components/selected-name.hbs"
});